import { Text, View, TouchableOpacity } from 'react-native'
import React, { Component } from 'react'
import Contador from '../../components/Contador'
class Home extends Component {
    constructor(props){
        super(props)
        this.state={
            conteo:0
        }
    }
    saludo(parametro){
        console.log(parametro)
    }
    click(){
        this.setState({
            conteo: this.state.conteo + 1
        })
    }

  render() {
    return (
      <View>
          <Text>Mi primer contador en RN</Text>
        <TouchableOpacity onLongPress={()=> this.saludo('Pase como parametro este gran saludo a la consola')}>
            <Text>Clickeame</Text>
        </TouchableOpacity>
        <Contador styles={estilosCss.contador} infoConteo={this.state.conteo} aumentar={()=> this.click()}/>
      </View>
    )
  }
}

const estilosCss ={
    container:{

    },
    contador:{
        text:{},
        button:{}

    }
}

export default Home
